acronymTemplate="<header id=\"subheader\">  <div class=\"container\">  <div class=\"row\">    <div class=\"col-sm-6\">    <ol class=\"breadcrumb\">      <li class=\"active\">Acronyms</li>    </ol>    <span class=\"collapse-link visible-xs xs-expand-link\" data-toggle=\"collapse\" data-target=\"#subheader-collapseable\" aria-expanded=\"false\" aria-controls=\"subheader-collapseable\"><span class=\"glyphicon glyphicon-triangle-bottom\"></span></span>    </div>    {{#if mraOutputs.assessment.targetLevel}}      <div class=\"col-sm-6 hidden-xs\">        <div class=\"row\">        <div class=\"col-xs-12 meta\">                  Target MRL: {{mraOutputs.assessment.targetLevel}} <span class=\"text-divider\">|</span>Target Date: {{mraOutputs.assessment.targetDate}}                </div>        </div>      </div>    {{/if}}  </div>  {{#if mraOutputs.assessment.targetLevel}}    <div class=\"collapse\"  id=\"subheader-collapseable\">    <div class=\"row visible-xs\">      <div class=\"col-xs-12\">          Target MRL: {{mraOutputs.assessment.targetLevel}}<span class=\"text-divider\">|</span>Target Date: {{mraOutputs.assessment.targetDate}}      </div>    </div>    </div>  {{/if}}  </div></header><div class=\"container\" id=\"mainContent\">    <!-- Should add a search/filter in here -->		<div class=\"acronymTable row-striped\">    {{#each mraOutputs.acronyms}}  		<div><div class=\"row\">  			<div class=\"col-sm-3\">{{acronym}}</div>  			<div class=\"col-sm-9\">{{value}}</div>  		</div>  		</div>    {{/each}}	</div>  <input type='hidden' id='inputPage' value='acronymPage'></div>";
criteriaTemplate="<header id=\"subheader\">  <div class=\"container\">  <div class=\"row\">    <div class=\"col-sm-6\">    <ol class=\"breadcrumb\">      <li class=\"active\">Criteria</li>    </ol>    <span class=\"collapse-link visible-xs xs-expand-link\" data-toggle=\"collapse\" data-target=\"#subheader-collapseable\" aria-expanded=\"false\" aria-controls=\"subheader-collapseable\"><span class=\"glyphicon glyphicon-triangle-bottom\"></span></span>    </div>    {{#if mraOutputs.assessment.targetLevel}}      <div class=\"col-sm-6 hidden-xs\">        <div class=\"row\">        <div class=\"col-xs-12 meta\">                  Target MRL: {{mraOutputs.assessment.targetLevel}} <span class=\"text-divider\">|</span>Target Date: {{mraOutputs.assessment.targetDate}}                </div>        </div>      </div>    {{/if}}  </div>  {{#if mraOutputs.assessment.targetLevel}}    <div class=\"collapse\"  id=\"subheader-collapseable\">    <div class=\"row visible-xs\">      <div class=\"col-xs-12\">          Target MRL: {{mraOutputs.assessment.targetLevel}}<span class=\"text-divider\">|</span>Target Date: {{mraOutputs.assessment.targetDate}}      </div>    </div>    </div>  {{/if}}  </div></header><div class=\"container\" id=\"mainContent\">		<div class=\"row\">		<div class=\"col-sm-6\">			<a class=\"document-link\" href=\"#\"><span class=\"glyphicon glyphicon-file\"></span> MRL Desktop V2.4</a>		</div>		<div class=\"col-sm-6 milestoneCol\">			<span class=\"dl-label\">Milestones</span>			<dl class=\"milestoneList\">				<dt>A</dt>				<dd>MRL 5</dd>				<dt>B</dt>				<dd>MRL 7</dd>				<dt>C</dt>				<dd>MRL 9</dd>			</dl>		</div>		</div>	<div class=\"criteria-filter-head\">		<div class=\"filter-top\">		<div class=\"row\">			<div class=\"col-xs-12\">				<button class=\"criteria-controls filter-button collapsed\" data-toggle=\"collapse\" data-target=\"#collapseFilter\">Filter</button>					<!-- <div class=\"expand-controls pull-right\">			<a href=\"javascript:void(0)\" class=\"criteria-controls expand-all\"><span class=\"glyphicon glyphicon-collapse-down\"></span> Expand all</a>			<a href=\"javascript:void(0)\" class=\"criteria-controls collapse-all\"><span class=\"glyphicon glyphicon-collapse-up\"></span> Collapse all</a>			</div> -->			</div>		</div>		</div>		<div class=\"criteria-filter-container collapse\" id=\"collapseFilter\">		<!-- <div class=\"filter-block\">		<div class=\"row\">			<div class=\"col-xs-12\">			<div class=\"filter-label\">Filter by Thread</div>			</div>		</div>		<div class=\"row\">			<div class=\"col-sm-3\">				<div class=\"checkbox\">				    <label>				      <input type=\"checkbox\"> Technology Maturity				    </label>				  </div>				  <div class=\"checkbox\">				      <label>				        <input type=\"checkbox\"> Technology &amp; Industrial Base				      </label>				    </div>				    <div class=\"checkbox\">				        <label>				          <input type=\"checkbox\"> Design				        </label>				      </div>			</div>			<div class=\"col-sm-3\">				<div class=\"checkbox\">				    <label>				      <input type=\"checkbox\"> Technology Maturity				    </label>				  </div>				  <div class=\"checkbox\">				      <label>				        <input type=\"checkbox\"> Technology &amp; Industrial Base				      </label>				    </div>				    <div class=\"checkbox\">				        <label>				          <input type=\"checkbox\"> Design				        </label>				      </div>			</div>			<div class=\"col-sm-3\">				<div class=\"checkbox\">				    <label>				      <input type=\"checkbox\"> Technology Maturity				    </label>				  </div>				  <div class=\"checkbox\">				      <label>				        <input type=\"checkbox\"> Technology &amp; Industrial Base				      </label>				    </div>				    <div class=\"checkbox\">				        <label>				          <input type=\"checkbox\"> Design				        </label>				      </div>			</div>			<div class=\"col-sm-3\">				<div class=\"checkbox\">				    <label>				      <input type=\"checkbox\"> Manufacturing Management				    </label>				  </div>				 </div>			</div>		</div> -->		<div class=\"filter-block form-inline\">		<div class=\"row \">		<div class=\"col-xs-12\">		<div class=\"filter-label\">Filter by MRL</div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-init='mrl_all=true' ng-model='mrl_all'>All</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_0'>1</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_1'>2</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_2'>3</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_3'>4</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_4'>5</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_5'>6</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_6'>7</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_7'>8</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_8'>9</label>      </div>      <div class=\"checkbox\">        <label><input type=\"checkbox\" ng-checked='mrl_all' ng-model='mrl_9'>10</label>      </div>		</div>		</div>		</div>		</div>	</div>	<div class=\"criteria-list\">      {{#each mraOutputs.threads}}		 <div class=\"navigation-group\">			      <h4 class=\"group-title\">			        <button role=\"button\" data-toggle=\"collapse\" data-target=\"#collapseThread{{@index}}\" aria-expanded=\"true\" aria-controls=\"collapseThread{{@index}}\" class=\"collapse-item collapsed\">			          {{name}}			        </button>			      </h4>			    <ul id=\"collapseThread{{@index}}\" class=\"collapse\" role=\"tabpanel\" aria-labelledby=\"headingOne\">            {{#each subThreads}}			     <li>			     	<button role=\"button\" data-toggle=\"collapse\" data-target=\"#collapseSubThread_{{@../index}}_{{@index}}\" aria-expanded=\"true\" aria-controls=\"collapseSubThread_{{@../index}}_{{@index}}\" class=\"collapse-item collapsed\">			     	  {{name}}			     	</button>			     	<dl id=\"collapseSubThread_{{@../index}}_{{@index}}\" class=\"collapse\">              {{#each subThreadLevels}}                {{#if criteriaText}}    							<dt level-id=\"mrl_{{@index}}\">MRL {{mrlLevel}}</dt>    							<dd level-id=\"mrl_{{@index}}\">{{{criteriaText}}}</dd>                {{/if}}              {{/each}}			     	</dl>			     	<!-- <ul id=\"collapseTwo\" class=\"collapse\">							<li><a role=\"button\" data-toggle=\"collapse\" href=\"#collapseThree\" aria-expanded=\"true\" aria-controls=\"collapseThree\" class=\"collapse-item collapsed\">			     	  Technology Transition to Production			     	</a>							<ul id=\"collapseThree\" class=\"collapse\">								<li><a href=\"#\">Question 1</a></li>								<li><a href=\"#\">Question 2</a></li>								<li><a href=\"#\">Question 3</a></li>								<li><a href=\"#\">Question 4</a></li>							</ul>			     	</li>							<li>3</li>							<li>4</li>							<li>5</li>			     	</ul> -->			     	</li>            {{/each}}			    </ul>			 </div>       {{/each}}	</div>  <input type='hidden' id='inputPage' value='criteriaPage'><script type=\"text/javascript\">  $(document).ready(function() {      var checkBoxes = document.querySelectorAll('input[ng-model*=mrl_]');      var tds = document.querySelectorAll('[level-id*=mrl_]');      for (var i=0; i<checkBoxes.length; i++) {        var checkBox = checkBoxes[i];        var ngModel = checkBox.getAttribute('ng-model');        if (ngModel == 'mrl_all') {            checkBox.addEventListener(\"click\", allClicked);        } else {            checkBox.addEventListener(\"click\", singleLevelClicked);        };      };      function allClicked(event) {          if (!event.target.checked) {              for (var j=0; j<tds.length; j++) {                var td = tds[j];                td.style.display = \"none\";              };          } else {              for (var j=0; j<tds.length; j++) {                var td = tds[j];                td.style.display = \"\";                td.checked = true;              };              for (var k=0; k<checkBoxes.length; k++) {                var box = checkBoxes[k];                box.checked = true;              };          };      };  });  function singleLevelClicked(event) {      var allCheckbox = document.querySelector('[ng-model=mrl_all]');      allCheckbox.checked = false;      var clickedLevel = this.getAttribute('ng-model');      var levelTDs = document.querySelectorAll('[level-id=' + clickedLevel + ']');      var clickedOn = event.target.checked;      for (l=0; l<levelTDs.length; l++) {        var box = levelTDs[l];        changeDisplay(box, clickedOn);      };  };  function changeDisplay(element, on) {      if (!on) {          element.style.display = \"none\"      } else {          element.style.display = \"\"      };  }</script>";
dashboardTemplate="<div class='container-fluid'>  <div class=\"mra_panel\">    <div class=\"mra_page_title\">      <h2>Dashboard</h2>      <hr>    </div>    <div class='question' align='center'>  		{{mraOutputs.assessment.scope}}  </div>    <div align='center'>      <table id='dashboardtable' class='table table-responsive' style='max-width:85%; background: #f5f5f5;' align='center'>      <th align='center'>Thread</th><th>Sub Thread</th>        <th class=\"t-centered\">1</th>        <th class=\"t-centered\">2</th>        <th class=\"t-centered\">3</th>        <th class=\"t-centered\">4</th>        <th class=\"t-centered\">5</th>        <th class=\"t-centered\">6</th>        <th class=\"t-centered\">7</th>        <th class=\"t-centered\">8</th>        <th class=\"t-centered\">9</th>        <th class=\"t-centered\">10</th>        <th>Completion Date</th>      {{#each mraOutputs.threadStatus}}          <tr class=\"dash\"><td class=\"dash\">{{threadName}}</td><td class=\"dash\">{{subThreadName}}</td>            {{#each statuses}}              <td class=\"dash t-centered\" ng-show='{{this}}==0' style='background-color:#cfd8dc'></td>              <td class=\"dash t-centered\" ng-show='{{this}}==1' style='background-color:#77c577; font-size: 1.6em;'>&#x2713;</td>              <!-- <td class=\"dash\" ng-show='{{this}}==2' style='background-color:red'><span class='glyphicon glyphicon-remove'></span></td> -->              <td class=\"dash t-centered\" ng-show='{{this}}==2' style='background-color:#c57777; font-size: 1.8em;'>&#x2715;</td>              <td class=\"dash t-centered\" ng-show='{{this}}==3' style='background-color:#779dc5'>&#x2796;</td>            {{/each}}            <td class=\"dash\">{{date}}</td>            </tr>      {{/each}}      </table>    </div>    <br>    <br>    <div align='center'>      <a href onclick='$(\"#functionToCall\").val(\"getMmpPage\")' ng-click='run()'><button class=\"btn btn-primary buttonRounded\" style='margin:10px'>Action Items</button></a>      <a href onclick='$(\"#functionToCall\").val(\"getNotApplicablePage\")' ng-click='run()'><button class=\"btn btn-primary buttonRounded\" style='margin:10px'>Not Applicable Items</button></a>      <a href onclick='$(\"#functionToCall\").val(\"getSkippedPage\")' ng-click='run()'><button class=\"btn btn-primary buttonRounded\" style='margin:10px'>Skipped Items</button></a>      <a href id='saveAsImage'><button class=\"btn btn-primary\" style='margin:10px'>Save as image</button></a>    </div>    <input type='hidden' id='inputPage' value='dashboardPage'>  </div>  <div id=\"img-out\">  </div></div><script>  $( document ).ready(function() {    var saveButton = document.getElementById('saveAsImage');    saveButton.addEventListener('click', function() {      html2canvas($(\"#dashboardtable\"), {        onrendered: function(canvas) {          theCanvas = canvas;          canvas.id = 'dashCanvas';          canvas.style.display = 'none';          var imgDiv = document.querySelector('#img-out');          imgDiv.appendChild(canvas);          promptSaveFile(canvas);        }      });    });    function promptSaveFile(canvas) {      try {        window.navigator.msSaveBlob(canvas.msToBlob(), \"MRAdashboard.png\");      } catch(err) {        canvas.toBlob(function(blob) {          blobUrl = window.URL.createObjectURL(blob);          imgLink = createHiddenDownloadLink(blobUrl);          imgLink.click();        });      };    };    function createHiddenDownloadLink(blobUrl) {      var imgLink = document.createElement(\"a\");      imgLink.download = \"MRAdashboard.png\";      imgLink.href = blobUrl;      imgLink.style.display = 'none';      document.querySelector('#img-out').appendChild(imgLink);      return imgLink;    };  });</script>";
definitionTemplate="<header id=\"subheader\">  <div class=\"container\">  <div class=\"row\">    <div class=\"col-sm-6\">    <ol class=\"breadcrumb\">      <li class=\"active\">Definitions</li>    </ol>    <span class=\"collapse-link visible-xs xs-expand-link\" data-toggle=\"collapse\" data-target=\"#subheader-collapseable\" aria-expanded=\"false\" aria-controls=\"subheader-collapseable\"><span class=\"glyphicon glyphicon-triangle-bottom\"></span></span>    </div>    {{#if mraOutputs.assessment.targetLevel}}      <div class=\"col-sm-6 hidden-xs\">        <div class=\"row\">        <div class=\"col-xs-12 meta\">                  Target MRL: {{mraOutputs.assessment.targetLevel}} <span class=\"text-divider\">|</span>Target Date: {{mraOutputs.assessment.targetDate}}                </div>        </div>      </div>    {{/if}}  </div>  {{#if mraOutputs.assessment.targetLevel}}    <div class=\"collapse\"  id=\"subheader-collapseable\">    <div class=\"row visible-xs\">      <div class=\"col-xs-12\">          Target MRL: {{mraOutputs.assessment.targetLevel}}<span class=\"text-divider\">|</span>Target Date: {{mraOutputs.assessment.targetDate}}      </div>    </div>    </div>  {{/if}}  </div></header><div class=\"container\" id=\"mainContent\">		<!-- TODO add search/jump in -->		<dl class=\"definitions\">      {{#each mraOutputs.definitions}}    		<dt>{{term}}</dt>    		<dd>{{definition}}</dd>      {{/each}}		</dl>	</div>";
